mapper.insert:
insert
  into tb_appr_mem
  (
    appr_no
  , emp_no
  , appr_level
  )
  values
  (
    'fitty-2022-00' || seq_apno.currval
  , #{empNo}
  , #{apprLevel}
  )

insert
  into tb_appr_vacation
  (
    appr_no
  , vct_status
  , vct_start_date
  , vct_end_date
  , vct_reason
  , vct_count
  )
  values
  (
    'fitty-2022-00' || seq_apno.currval
  , #{vctStatus}
  , #{vctStartDate}
  , #{vctEndDate}
  , #{vctReason}
  , #{vctCount}
  )

insert 
  into tb_appr_overtime
  (
    appr_no
  , ovt_date
  , ovt_start_time
  , ovt_end_time
  , ovt_reason
  )
  values
  (
    'fitty-2022-00' || seq_apno.currval
  , #{ovtDate}
  , #{ovtStartTime}
  , #{ovtEndTime}
  , #{ovtReason}
  )

insert
  into tb_appr_exp
  (
    appr_no
  , exp_mem
  , exp_date
  , exp_total_amount
  , exp_reason
  )
  values
  (
    'fitty-2022-00' || seq_apno.currval
  , #{expMem}
  , #{expDate}
  , #{expTotalAmount}
  , #{expReason}
  )

insert
  into tb_exp_detail
  (
    appr_no
  , exp_date
  , exp_status
  , exp_amount
  , exp_history
  , exp_note
  )
  values
  (
    'fitty-2022-00' || seq_apno.currval
  , #{expDate}
  , #{expStatus}
  , #{expAmount}
  , #{expHistory}
  , #{expNote}
  )

mapper.select:
select
   	   count (*)
  from tb_approval
 where emp_no = #{empNo}
   and appr_status = #{apprStatus}
   and appr_storage = 'N'

select
   	   count (*)
  from tb_approval ap
  join tb_appr_mem am on(am.appr_no = ap.appr_no)
 where am.emp_no = #{empNo}
   and ap.appr_status = #{apprStatus}

mapper.update:
update tb_approval
   set appr_status = #{apprStatus}
 where appr_no = #{apprNo}

update tb_appr_mem
   set appr_date = to_char(sysdate, 'YYYY-MM-DD')
     , appr_status = 'Y'
 where appr_no = #{apprNo}
   and emp_no = #{empNo}

update tb_att
   set att_status = #{vctStatus}
 where emp_no = #{empNo}
   and att_date between #{vctStartDate} and #{vctEndDate}

update tb_att
   set att_status = 'P'
     , att_plus_work = 'P'
 where empNo = #{empNo}
   and att_date = #{ovtDate}

update tb_approval
   set appr_status = 4
     , appr_comment = #{apprComment}
 where appr_no = #{apprNo}

update tb_appr_mem
   set appr_status = 'N'
     , appr_comment = #{apprComment}
 where appr_no = #{apprNo}
   and emp_no = #{empNo}
