mapper.insert:
insert
  into tb_appr_mem
  (
    appr_no
  , emp_no
  , appr_level
  )
  values
  (
    'fitty-2022-00' || seq_apno.currval
  , #{empNo}
  , #{apprLevel}
  )

insert
  into tb_appr_vacation
  (
    appr_no
  , vct_status
  , vct_start_date
  , vct_end_date
  , vct_reason
  , vct_count
  )
  values
  (
    'fitty-2022-00' || seq_apno.currval
  , #{vctStatus}
  , #{vctStartDate}
  , #{vctEndDate}
  , #{vctReason}
  , #{vctCount}
  )

insert 
  into tb_appr_overtime
  (
    appr_no
  , ovt_date
  , ovt_start_time
  , ovt_end_time
  , ovt_reason
  )
  values
  (
    'fitty-2022-00' || seq_apno.currval
  , #{ovtDate}
  , #{ovtStartTime}
  , #{ovtEndTime}
  , #{ovtReason}
  )

mapper.select:
select
   	   count (*)
  from tb_approval
 where emp_no = #{empNo}
   and appr_status = #{apprStatus}
   and appr_storage = 'N'

select
   	   count (*)
  from tb_approval ap
  join tb_appr_mem am on(am.appr_no = ap.appr_no)
 where am.emp_no = #{empNo}
   and ap.appr_status = #{apprStatus}
