mapper.insert:
insert
  into tb_class
  (
  	cl_no
  , emp_no
  , user_no
  , user_height
  , user_weight
  , cl_sdate
  , cl_count
  , cl_goal
  , cl_result
  )
  values
  (
  	seq_clno.nextval
  , #{empNo}
  , #{userNo}
  , #{userHeight}
  , #{userWeight}
  , #{classStartDate}
  , #{classCount}
  , #{classGoal}
  , #{classResult}
  )

insert
  into tb_exercise
  (
  	cl_no
  , ex_date
  , ex_writer
  , ex_title
  , ex_count
  , ex_set
  , ex_coment
  , ex_link
  , ex_status
  , ex_no
  )
  values
  (
  	#{classNo}
  , to_char(sysdate, 'YY/MM/DD')
  , #{exWriter}
  , #{exTitle}
  , #{exCount}
  , #{exSet}
  , #{exComent}
  , #{exLink}
  , 'N'
  , seq_exno.nextval
  )

mapper.update:
update tb_exercise
   set ex_status = 'Y'
 where ex_no = #{exNo}

update tb_exercise
   set ex_title = #{exTitle}
     , ex_count = #{exCount}
     , ex_set = #{exSet}
     , ex_coment = #{exComent}
     , ex_status = 'N'
 where ex_no = #{exNo}

